mapper.sql:

  item.is_disable = FALSE

    and item.item_code = #{itemCode,jdbcType=VARCHAR}


    and item.`name` = #{name,jdbcType=VARCHAR}


    and item.`short_title` = #{shortTitle,jdbcType=VARCHAR}


    and item.supplier_id = #{supplierId,jdbcType=BIGINT}


    and item.category_id = #{categoryId,jdbcType=BIGINT}


    and item.`catalog_id` = #{catalogId,jdbcType=BIGINT}


    and item.`brand_id` = #{brandId,jdbcType=BIGINT}


    and item.`keywords` = #{keywords,jdbcType=VARCHAR}


    and item.`is_virtual` = #{isVirtual,jdbcType=BIT}


    and item.`seller_note` = #{sellerNote,jdbcType=VARCHAR}


ORDER BY item.create_time DESC

mapper.select:
SELECT

FROM

WHERE item.is_disable = false and item.id = #{id}

SELECT

FROM

WHERE item.is_disable = false and item.item_code = #{itemCode}
